import styled from 'styled-components';
import {MdKeyboardArrowRight, MdArrowForward} from 'react-icons/md';

export const HeroContainer = styled.div`
    background: #0c0c0c;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0 30px;
    height: 800px;
    position: relative;
    z-index: 1;
`

export const HeroBg = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
`

export const VideoBg = styled.video`
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit: cover;
    background: #232a34
`

export const HeroContent = styled.div`
    z-index: 3;
    max-width: 1200px;
    position: absolute;
    padding 8px 24px;
    display: flex;
    flex-direction: column;
    align-items: center;
`


/*  color: #fff;
    font-size: 48px;
    text-align: center;
    text-shadow: 5px 5px 10px black;

    @media screen and (max-width: 768px) {
        font-size: 40px;
    }

    @media screen and (max-width: 480px) {
        font-size: 32px;
    }
    
    */
export const HeroH1 = styled.h1`
position: relative;
z-index: 2;
font-size: 10em;
letter-spacing: 15px;
text-transform: uppercase;
transform: rotate(-10deg);
text-shadow: 1px 1px hsl(50, 50%, 45%),
             2px 2px hsl(50, 50%, 40%),
             3px 3px hsl(50, 50%, 35%),
             4px 4px hsl(50, 50%, 34%),
             5px 5px hsl(50, 50%, 33%),
             6px 6px hsl(50, 50%, 32%),
             7px 7px hsl(50, 50%, 31%),
             8px 8px hsl(50, 50%, 30%),
             9px 9px hsl(50, 50%, 29%),
             10px 10px hsl(50, 50%, 28%),
             10px 10px 30px rgba(0,0,0,.7),
`

export const HeroP = styled.p`
    margin-top: 24px;
    color: #fff;
    font-size: 24px;
    text-align: center;
    max-width: 600px;
    text-shadow: 1px 1px 2px black;

    @media screen and (max-width: 768px) {
        font-size: 24px;
    }

    @media screen and (max-width: 480px) {
        font-size: 18px;
    }
`

export const HeroBtnWrapper = styled.div`
    margin-top: 32px;
    display: flex;
    flex-direction: column;
    align-items: center;
`

export const ArrowForward = styled(MdArrowForward)`
    margin-left: 8px;
    font-size: 20px;
`

export const ArrowRight = styled(MdKeyboardArrowRight)`
    margin-left: 8px;
    font-size: 20px;
`
