import React, {useState} from 'react';
import Video from '../../videos/grabcontroller.mp4';
import {SUButton} from '../ButtonElement';
import { HeroContainer, HeroBg, VideoBg, HeroContent, HeroH1, HeroP, HeroBtnWrapper, ArrowForward, ArrowRight} from './HeroElements';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../App.css';

const HeroSection = () => {
    const [hover, setHover] = useState(false)

    const onHover = () => {
        setHover(!hover)
    }
      
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>

        <HeroContainer>
            <HeroBg>
                <VideoBg autoPlay loop muted src={Video} type='video/mp4' blurRadius={100}/>
            </HeroBg>
            <HeroContent>
                <HeroH1>Title Text Here</HeroH1>
                <HeroP>
                    Caption Text Here
                </HeroP>
                <HeroBtnWrapper>
                    <SUButton to='signup' onMouseEnter={onHover} onMouseLeave={onHover}
                    primary='true' dark='true' onClick={handleShow}>
                        Sign Up {hover ? <ArrowForward /> : <ArrowRight />}
                    </SUButton>
                </HeroBtnWrapper>
            </HeroContent>
        </HeroContainer>

        <Modal 
        show={show} 
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
        >
        <Modal.Header closeButton>
            <Modal.Title><div class="header"><h1>Start finding friends today!</h1></div></Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Group controlId="formUsername">
                    <Form.Text>What's your username?</Form.Text>
                    <Form.Control type="text" placeholder="Username" />
                </Form.Group>
                <div class="pad"><p>       </p></div>
                <Form.Group controlId="formBasicEmail">
                    <Form.Text>What's your email?</Form.Text>
                    <Form.Control type="email" placeholder="Email Address" />
                </Form.Group>
                <div class="pad"><p>       </p></div>
                <Form.Group controlId="formBasicPassword">
                    <Form.Control type="password" placeholder="Password" />
                </Form.Group>
                <div class="pad"><p>       </p></div>
                <Form.Group controlId="formBasicPassword">
                    <Form.Control type="password" placeholder="Confirm Password" />
                </Form.Group>
                <div class="pad"><p>       </p></div>
                <Button variant="primary" type="Sign Up">
                    Submit
                </Button>
            </Form>
        </Modal.Body>
        </Modal>

      </>
    )
}

export default HeroSection
